      }
    },
    "SchemaDefinition": {
      "tenantId": "pg",
      "code": "localization.master",
      "description": "Localization Master Schema for Multi-language Translations",
      "isActive": true,
      "definition": {
        "type": "object",
        "$schema": "http://json-schema.org/draft-07/schema#",
        "required": [
          "module",
          "code",
          "locale"
        ],
        "x-unique": [
          "code",
          "locale"
        ],
        "properties": {
          "module": {
            "type": "string",
            "minLength": 1,
            "maxLength": 100,
            "description": "Module name (e.g., rainmaker-pgr, rainmaker-common)"
          },
          "code": {
            "type": "string",
            "minLength": 1,
            "maxLength": 200,
            "description": "Translation key (e.g., PGR_COMPLAINT_TYPE, CORE_COMMON_SAVE)"
          },
          "englishMessage": {
            "type": ["string", "null"],
            "maxLength": 1000,
            "description": "English translation message"
          },
          "locale": {
            "type": "string",
            "pattern": "^[a-z]{2}_[A-Z]{2}$",
            "description": "Locale code in format: language_COUNTRY (e.g., en_IN, hi_IN, ta_IN)"
          },
          "translation": {
            "type": ["string", "null"],
            "maxLength": 1000,
            "description": "Translated message in the specified locale"
          }
        }
      }
    }
  }

