      }
    },
    "SchemaDefinition": {
      "tenantId": "pg",
      "code": "common.master",
      "description": "Common Master Schema - New Format (Search Words Optional, Priority Removed)",
      "isActive": true,
      "definition": {
        "type": "object",
        "$schema": "http://json-schema.org/draft-07/schema#",
        "required": [
          "departmentName",
          "designationName",
          "complaintType",
          "complaintSubType",
          "resolutionTimeHours"
        ],
        "x-unique": ["complaintSubType"],
        "properties": {
          "departmentName": {
            "type": "string",
            "minLength": 2,
            "maxLength": 100,
            "description": "Name of the government department"
          },
          "designationName": {
            "type": "string",
            "minLength": 2,
            "maxLength": 100,
            "description": "Job title/position within the department"
          },
          "complaintType": {
            "type": "string",
            "pattern": "^[A-Za-z0-9_ &-]+$",
            "minLength": 2,
            "maxLength": 100,
            "description": "Parent complaint category"
          },
          "complaintSubType": {
            "type": "string",
            "pattern": "^[A-Za-z0-9_ &-]+$",
            "minLength": 2,
            "maxLength": 100,
            "description": "Specific complaint sub-type (must be unique)"
          },
          "resolutionTimeHours": {
            "type": "number",
            "minimum": 0.5,
            "maximum": 720,
            "description": "SLA hours for resolution"
          },
          "searchWords": {
            "type": ["string", "null"],
            "pattern": "^[A-Za-z0-9_, &-]*$",
            "maxLength": 500,
            "description": "Comma-separated keywords (optional)"
          }
        },
        "additionalProperties": false
      }
    }
  }

