      "SchemaDefinition": {
        "tenantId": "pg",
        "code": "tenant.master",
        "description": "Tenant Master Schema - Simple format with City and District",
        "isActive": true,
        "definition": {
          "type": "object",
          "$schema": "http://json-schema.org/draft-07/schema#",
          "required": [
            "tenantDisplayName",
            "tenantCode",
            "tenantType",
            "logoFilePath"
          ],
          "x-unique": ["tenantCode"],
          "properties": {
            "tenantDisplayName": {
              "type": "string",
              "minLength": 2,
              "maxLength": 200
            },
            "tenantCode": {
              "type": "string",
              "pattern": "^[A-Z]+\\.[A-Z]+$|^[A-Z]+$"
            },
            "tenantType": {
              "type": "string",
              "enum": ["State", "City"]
            },
            "logoFilePath": {
              "type": "string",
              "minLength": 1,
              "maxLength": 500
            },
            "cityName": {
              "type": ["string", "null"],
              "maxLength": 100
            },
            "districtName": {
              "type": ["string", "null"],
              "maxLength": 100
            },
            "address": {
              "type": ["string", "null"],
              "maxLength": 500
            },
            "tenantWebsite": {
              "type": ["string", "null"]
            },
            "latitude": {
              "type": ["number", "null"],
              "minimum": -90,
              "maximum": 90
            },
            "longitude": {
              "type": ["number", "null"],
              "minimum": -180,
              "maximum": 180
            }
          },
          "additionalProperties": false
        }
      }
    }

