  "SchemaDefinition": {
    "tenantId": "pg",
    "code": "common.master",
    "description": "Common Master Schema for Departments, Designations, and Complaint Types",
    "isActive": true,
    "definition": {
      "type": "object",
      "$schema": "http://json-schema.org/draft-07/schema#",
      "required": [
        "departmentName",
        "designationName",
        "complaintType",
        "complaintSubType",
        "resolutionTimeHours",
        "searchWords"
      ],
      "x-unique": ["complaintSubType"],
      "properties": {
        "departmentName": {
          "type": "string",
          "minLength": 2,
          "maxLength": 100,
          "description": "Name of the government department (e.g., WATER DEPARTMENT, HEALTH DEPARTMENT)"
        },
        "designationName": {
          "type": "string",
          "minLength": 2,
          "maxLength": 100,
          "description": "Job title/position within the department (e.g., Engineer, Supervisor)"
        },
        "complaintType": {
          "type": "string",
          "pattern": "^[A-Za-z0-9_ &-]+$",
          "minLength": 2,
          "maxLength": 100,
          "description": "Parent complaint category"
        },
        "complaintSubType": {
          "type": "string",
          "pattern": "^[A-Za-z0-9_ &-]+$",
          "minLength": 2,
          "maxLength": 100,
          "description": "Specific complaint sub-type name (must be unique)"
        },
        "resolutionTimeHours": {
          "type": "number",
          "minimum": 0.5,
          "maximum": 720,
          "description": "SLA hours for resolution"
        },
        "searchWords": {
          "type": "string",
          "pattern": "^[A-Za-z0-9_, &-]+$",
          "description": "Comma-separated keywords for search"
        },
        "priority": {
          "type": ["integer", "null"],
          "minimum": 1,
          "maximum": 10,
          "description": "Priority level (1=highest, 10=lowest)"
        }
      }
    }
  }
}

